package net.link.safeonline.webapp.common;import org.apache.wicket.Page;import org.apache.wicket.Response;import org.apache.wicket.protocol.http.PageExpiredException;import org.apache.wicket.protocol.http.WebApplication;import org.apache.wicket.protocol.http.WebRequest;import org.apache.wicket.protocol.http.WebRequestCycle;public final class MyRequestCycle extends WebRequestCycle {    /**     * MyRequestCycle constructor     *      * @param application     *            the web application     * @param request     *            the web request     * @param response     *            the web response     */    public MyRequestCycle(final WebApplication application, final WebRequest request, final Response response) {        super(application, request, response);    }    /**     * {@inheritDoc}     */    @Override    public final Page onRuntimeException(final Page cause, final RuntimeException e) {        if (e instanceof PageExpiredException)            return null;        return new ErrorPage();    }}